        label="select"
        enableMultiple={true}
        enableSearch={false}
        placeholder={"choose option"}
        defaultValue={[]}
        options={[
          { label: "Group A", value: "Group A", group: true },
          { label: "a", value: "a" },
          { label: "Group B", value: "Group B", group: true },
          { label: "b", value: "b" },
        ]}
      />
    </div>
  );
}
