import React, { useState } from "react";
import Select from "react-select";
import makeAnimated from "react-select/animated";

export default function Home({ header, activity, handleSelectChange }) {
  const optionsActivities = [
    { value: "yoga", label: "Yoga" },
    { value: "meditation", label: "Meditation" },
    { value: "cardio", label: "Cardio" },
  ];

  return (
    <div class="home">
      <p>
        Down to meditate? Into yoga? Always on the move? Log your self-care
        sessions using the menu below, and keep track of your kick-ass routines
        as you practice. Or, add a new routine entirely. You do you!
      </p>
      <Select />
    </div>
  );
}


